(153, 'pt-br', 'Cursos de Graduação- Campus Dom Bosco', '<font face=\"Arial\">Localizado na cidade de São João del-Rei, o&nbsp;Campus Dom Bosco possui os seguintes cursos de graduação:</font><div><br></div><div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=37\" target=\"_self\">História</a><div><div><div><div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=34\" target=\"_self\">Letras</a></div><div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=35\" target=\"_self\">Letras- Língua Inglesa e suas Literaturas</a></div></div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=38\" target=\"_self\">Medicina</a><div></div><div><div><div><div><div><span style=\"color: rgb(34, 34, 34); background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><font face=\"Arial\"></font></span></div></div></div></div></div><div><br></div></div></div></div>', 39),
(154, 'en-us', 'Undergraduate Programs- Dom Bosco Campus', '<div>Located in the city of São João del-Rei, Dom Bosco Campus has the following undergraduate programs:&nbsp;</div><div><br></div><div><div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=35\" target=\"_self\" style=\"color: rgb(0, 86, 179); text-decoration-line: underline; background-color: rgb(255, 255, 255);\">English as a Foreign Language and English Language Literatures</a></div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=37\" target=\"_self\">History</a></div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=38\" target=\"_self\">Medicine</a><div><div><div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=34\" target=\"_self\">Portuguese Language and its Literatures&nbsp;</a></div></div></div><div><br></div>', 39),
