(173, 'pt-br', 'Cursos de Graduação- Campus Santo Antônio', '<div>Localizado na cidade de São João del-Rei, o Campus Santo Antônio possui os seguintes cursos de graduação:</div><div><br></div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=40\" target=\"_self\">Engenharia Mecânica</a><div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=43\" target=\"_self\">Engenharia Elétrica</a><div></div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=41\" target=\"_self\">Bacharelado em Matemática</a><div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=42\" target=\"_self\">Licenciatura em Matemática</a><div></div></div></div>', 44),
(174, 'en-us', 'Undergraduate Programs- Santo Antônio Campus', 'Located in the city of São João del-Rei, Santo Antônio Campus has the following undergraduate programs:<div><br></div><div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=43\" target=\"_self\">Electrical Engineering</a><div></div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=40\" target=\"_self\">Mechanical Engineering</a><div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=41\" target=\"_self\">Bachelors in Mathematics</a></div><div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=42\" target=\"_self\">Bachelors Degree in Mathematics with Licensing&nbsp;</a><div></div><div><br></div><div><br></div><div><br></div></div></div>', 44),
