(193, 'pt-br', 'Cursos de Graduação- Campus Tancredo Neves', '<font face=\"Arial\">Localizado na cidade de São João del-Rei, o&nbsp;Campus Tancredo Neves possui os seguintes cursos de graduação:</font><div><font face=\"Arial\"><br></font></div><div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=50\" target=\"_self\"><font face=\"Arial\">Arquitetura</font></a><div><font face=\"Arial\"></font></div><div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=48\" target=\"_self\"><font face=\"Arial\">Licenciatura em Música</font></a><div><font face=\"Arial\"></font></div></div></div>', 49),
(194, 'en-us', 'Undergraduate Programs- Tancredo Neves Campus', '<div><div><font face=\"Arial\">Located in the city of São João del-Rei, Tancredo Neves Campus has the following undergraduate programs:&nbsp;</font></div></div><div><font face=\"Arial\"><br></font></div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=50\" target=\"_self\"><font face=\"Arial\">Bachelor\'s Degree in Architecture</font></a><div><font face=\"Arial\"></font></div><div><a href=\"http://192.168.2.175/page.php?type=3&amp;id=48\" target=\"_self\" style=\"font-size: 1rem;\"><font face=\"Arial\">Teaching Degree in Music</font></a><br></div>', 49),
